import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import { useSelector } from "react-redux";

import { URL } from "../../services/auth";
import { history } from "../../history";

import "../FormEdit/FormEdit.css";

const FormEdit = () => {
  const id = useSelector((state) => state.id);

  const [stock, setStock] = useState([]);
  console.log(stock);

  const createRegister = async (e) => {
    e.preventDefault();

    const { prod, amount, price, client, bool } = e.target;

    const data = {
      product: prod.value,
      quantity: amount.value,
      price: price.value,
      client: client.value,
      active: bool.value,
    };

    await axios({
      method: "post",
      url: URL,
      data,
    })
      .then((resp) => {
        alert("Editado com sucesso!");
        history.push("/");
      })
      .catch((err) => {
        alert("Houve um erro ao Editar o produto!");
      });
  };

  const loadData = useCallback(async (URL) => {
    await axios
      .get(URL)
      .then((resp) => {
        setStock(resp.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  useEffect(() => {
    loadData(`${URL}/${id}`);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const getValue = (event) => {
    const { prod, value } = event.target;

    console.log(event.target.value);
  };

  return (
    <div className="formContent">
      <h2>Edite o item do stock</h2>
      <form className="formControl" onSubmit={createRegister}>
        <label htmlFor="prod">Produto</label>
        <input
          onChange={getValue}
          value={stock.product}
          id="prod"
          type="text"
          placeholder="Iphone"
          autoFocus
          required
        />

        <label htmlFor="amount">Quantidade</label>
        <input
          value={stock.quantity}
          id="amount"
          min="1"
          max="1000000"
          type="number"
          placeholder="1"
          required
        />

        <label htmlFor="price">Pre√ßo</label>
        <input
          value={stock.price}
          id="price"
          type="number"
          min="0.01"
          max="10000.00"
          step="0.01"
          placeholder="0,00"
          required
        />

        <label htmlFor="client">Cliente</label>
        <input
          value={stock.client}
          id="client"
          type="text"
          placeholder="Maria Joana"
          required
        />
        <div>
          <label htmlFor="true">Ativo</label>
          <br></br>
          <input
            type="radio"
            id="true"
            value="true"
            name="bool"
            defaultChecked
          />
          <label htmlFor="false">Inativo</label>
          <br></br>
          <input type="radio" id="false" value="false" name="bool" />
        </div>
        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
};

export default FormEdit;
