import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";

import { history } from "../../history";

import { ENDPOINT_TOKEN } from "../../services/auth";

import "./Main.css";

const Main = () => {
  const [stock, setStock] = useState([
    {
      _id: "",
      product: "",
      quantity: 0,
      price: 0,
      client: "",
      active: false,
    },
  ]);

  const URL = `https://crudcrud.com/api/${ENDPOINT_TOKEN}/stock`;

  //this function, takes the data from the endpoint and presents it in the application
  const getStockData = useCallback(async (URL) => {
    await axios
      .get(URL)
      .then((resp) => {
        console.log(resp.data);
        if (resp.data[0].length === 0) {
          setStock({
            _id: "",
            product: "",
            quantity: 0,
            price: 0,
            client: "",
            active: false,
          });
        } else {
          setStock(resp.data);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  //this function, redirects to the edit page with the data to edit
  const editForm = () => {
    history.push("/form");
  };

  //this function, removes data from a line at the endpoint
  const deleteProduct = async (e, id) => {
    // await axios
    //   .delete(`${URL}/${id}`)
    //   .then((resp) => {
    //     setStock(resp.data);
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });

    console.log(document.getElementById("tableStock"));
    document.getElementById("tableStock").deleteRow(2);
  };

  //effect of loading the data presented on screen
  useEffect(() => {
    getStockData(URL);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className="tableList">
      <button id="create">Create a new product</button>
      <table id="tableStock" className="responsive-table">
        <thead>
          <tr className="tableTitle">
            {Object.keys(stock[0]).map((item) => {
              return <th key={item}>{item}</th>;
            })}
            <th> </th>
            <th> </th>
          </tr>
        </thead>
        <tbody>
          {Object.values(stock).map((name) => {
            return (
              <tr className="tableValues" key={name._id}>
                <td>{name._id}</td>
                <td>{name.product}</td>
                <td>{name.price}</td>
                <td>{name.quantity}</td>
                <td>{name.client}</td>
                <td>{name.active ? "Yes" : "No"}</td>
                <td>
                  <button id="select" onClick={editForm}>
                    Select
                  </button>
                </td>
                <td>
                  <button
                    id="delete"
                    onClick={(e) => {
                      deleteProduct(e, name._id);
                    }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default Main;
